package user;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import user.entity.User;@Controller    @RequestMapping("/hiber")    public class HibController {        @Autowired        private UserDao userDao;        @ResponseBody        @RequestMapping("/create")        public String create()        {            User u = new User();            u.setLoginName("tomas_m@tut.by");            u.setPasswdName("oihg");            userDao.create(u);            return "saved";        }        @ResponseBody        @RequestMapping("/read")        public String read(@RequestParam("id") int idN)        {            User read = userDao.read(idN);            System.out.println(read);            return "read";        }        @ResponseBody        @RequestMapping("/getUserByLogin")        public String read(@RequestParam("login") String l)        {            User read = userDao.getUserByLogin(l);            System.out.println(read);            return "getUserByLogin";        }}